// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../node_modules/@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "../node_modules/@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../node_modules/@openzeppelin/contracts/security/Pausable.sol";
import "../node_modules/@openzeppelin/contracts/access/AccessControl.sol";

/// @custom:security-contact laviniascmelo@gmail.com
contract PurpleButterflyToken is ERC20, ERC20Burnable, Pausable, AccessControl {
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 private constant NFT_CONTRACT = keccak256("NFT_CONTRACT");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    address private _pbfAddress;

    function setNftContractAddress(
        address contractAddress
    ) public onlyRole(MINTER_ROLE) {
        _pbfAddress = contractAddress;
    }


    function getNftContractAddress() public view returns (address) {
        return _pbfAddress;
    }

    constructor() ERC20("Purple Butterfly", "PBFTK") {
        _grantRole(NFT_CONTRACT, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _mint(msg.sender, 1000 * 10 ** decimals());
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function chargeUser(address from, address to, uint256 amount)
        public
        onlyRole(NFT_CONTRACT)
    {
        _transfer(from, to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }
}
